mapper.select:
	select * from trade_records where userId = #{userId} 

		and tradeType = #{tradeType}


	    and DATE_FORMAT(createTime,'%Y-%m-%d')>=#{start,jdbcType=VARCHAR}


	    and DATE_FORMAT(createTime,'%Y-%m-%d')<=#{end,jdbcType=VARCHAR}	


  		limit #{size,jdbcType=INTEGER},#{offset,jdbcType=INTEGER}

select count(0) from trade_records where userId = #{userId} 
<
	and tradeType = #{tradeType}


    and DATE_FORMAT(createTime,'%Y-%m-%d')>=#{start,jdbcType=VARCHAR}


    and DATE_FORMAT(createTime,'%Y-%m-%d')<=#{end,jdbcType=VARCHAR}
