mapper.select:
select

from lottery_issue
where kindOf = #{kindOf}
and openTime is not null

	and DATE_FORMAT(openTime,'%Y-%m-%d')>=#{start,jdbcType=VARCHAR}


	and DATE_FORMAT(openTime,'%Y-%m-%d')<=#{end,jdbcType=VARCHAR}

order by startTime desc

	limit #{size}


	,#{offset}

select

from lottery_issue
where kindOf = #{kindOf}

	and status = #{status}


	and DATE_FORMAT(endTime,'%Y%m%d') = #{day,jdbcType=VARCHAR}

order by startTime;

mapper.update:
update lottery_issue


		status = #{issue.status},


		openCode = #{issue.openCode},


		openTime = #{issue.openTime}


where kindOf = #{issue.kindOf} and expect = #{issue.expect}
