mapper.insert:
insert into guess_club (clubId, createId, maxMember, name, password,
cardNum, kindOf) values (
#{clubId}, #{createId}, #{maxMember}, #{maxMember}, #{name}, #{password},
#{cardNum}, kindOf
);

mapper.update:
update guess_club set
clubId = #{clubId}

	,maxMember = #{maxMember}


	,name = #{name}


	,password = #{password}


	,cardNum = #{cardNum}


	,icon = #{icon}


	,notice = #{notice}


	,currentMember = #{currentMember}


	,status = #{status}

where clubId = #{clubId} and createId = #{createId}
